function renderProductsCarousel(a,i,n){a.reverse().map((a,s)=>{$(i).trigger("add.owl.carousel",[`\n        <div class="item" data-id-product="${a.id}">\n            <div class="image">\n                <a href="/html-page-product-detail.html"><img src="${a.image[0]}" alt=""></a>\n                ${""!==a.status?`\n                <div class="product-status">\n                    <span>${a.status}</span>\n                </div>\n                `:""}\n                \n            </div>\n\n            <div class="item-content">\n                <div class="brand">\n                    <a href="">${a.brand}</a>\n                </div>\n                <div class="product-group">\n                    <a href="">\n                        <div class="evaluate">\n                            <i class="far fa-star"></i>\n                            <i class="far fa-star"></i>\n                            <i class="far fa-star"></i>\n                            <i class="far fa-star"></i>\n                            <i class="far fa-star"></i>\n                        </div>\n                        <p class="name">${a.name}</p>\n                    </a>\n                </div>\n                <div class="price">\n                    ${""!==a.status?`\n                    <span class="fixed-price">$${a.price-a.price*a.status.match(/\d/g).join("")/100}</span>\n                    <del class="origin-price">$${a.price}</del>\n                    `:`\n                    <span class="origin-price">$${a.price}</span>\n                    `}                    \n                    \n                </div>\n\n                ${a.id==n?`\n                <div class="parameters">\n                    <div class="choose-image-references">\n                        <button class="blueBorder"><img src="${a.image[0]}" alt=""></button>\n                        <button><img src="${a.image[1]}" alt=""></button>\n                    </div>\n                    <div class="choose-size">\n                        <button class="active">S</button>\n                        <button>M</button>\n                        <button>L</button>\n                    </div>\n                    <div class="choose-materials">\n                        <button class="blueBorder"><img src="dist/img/materials/material1.PNG" alt=""></button>\n                        <button><img src="dist/img/materials/material2.PNG" alt=""></button>\n                        <button><img src="dist/img/materials/material3.PNG" alt=""></button>                            \n                    </div>\n                </div>\n                `:""}\n\n                <div class="btn-add">\n                    <a href=""><i class="far fa-calendar"></i> ADD TO CARD</a>\n                </div>\n            </div>\n            <div class="invisible-part">\n                <ul>\n                    <li>\n                        <a href=""><i class="far fa-eye"></i></a>\n                        <div class="description">Quick View</div>\n                    </li>\n                    <li>\n                        <a href=""><i class="far fa-heart"></i></a>\n                        <div class="description">You need to login</div>\n                    </li>\n                    <li>\n                        <a href=""><i class="fas fa-balance-scale"></i></a>\n                        <div class="description">Add to Compare</div>\n                    </li>\n                </ul>\n            </div>\n        </div>\n        `,s]).trigger("refresh.owl.carousel")})}function DrawALine(a){$("body").css("position","relative"),$("body").prepend(`<div style="background-color: red; position: absolute; height: 1px; width: 100%; top: ${a}px; z-index: 999;"></div>`)}